import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { getToken, getUser, setUser, clearToken, isTokenValid, apiRequest } from '../utils/auth';
import dairyImage from '../assets/dairy.jpg';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();

  // Enhanced token and user data handling
  const [user, setUserState] = useState(getUser() || {});
  const [token, setTokenState] = useState(getToken());
  const [tokenValid, setTokenValid] = useState(true);

  // All useState calls must be at the top level
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [fieldErrors, setFieldErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [editForm, setEditForm] = useState({
    firstName: user.firstName || '',
    lastName: user.lastName || '',
    email: user.email || '',
    phone: user.phone || ''
  });

  // Check token validity on component mount
  useEffect(() => {
    const checkTokenValidity = () => {
      if (!isTokenValid()) {
        clearToken();
        setTokenValid(false);
        setTokenState(null);
        setUserState({});
        return false;
      }

      setTokenValid(true);
      setTokenState(getToken());
      return true;
    };

    checkTokenValidity();
  }, []);

  // Check if user is authenticated
  if (!tokenValid || !token || !user.id) {
    return (
      <div className="profile-container">
        <Navbar />
        <div className="profile-main">
          <div className="profile-content">
            <div className="message error">
              <span className="message-icon">⚠️</span>
              Please login to view your profile.
            </div>
            <button onClick={() => navigate('/login')} className="btn btn-primary">
              Go to Login
            </button>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  // Handle edit form changes
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle profile update
  const handleProfileUpdate = async (e) => {
    e.preventDefault();

    // Simple validation
    if (!editForm.firstName || !editForm.lastName || !editForm.email || !editForm.phone) {
      setMessage({ type: 'error', text: 'All fields are required' });
      return;
    }

    setIsLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const response = await apiRequest('http://localhost:5000/api/auth/profile', {
        method: 'PUT',
        body: JSON.stringify(editForm),
      });

      const data = await response.json();

      if (!response.ok) {
        // Handle backend validation errors
        if (data.errors) {
          // Set field-specific errors from backend
          setMessage({ 
            type: 'error', 
            text: 'Please fix the validation errors: ' + Object.values(data.errors).join(', ')
          });
          return;
        }
        throw new Error(data.message || 'Failed to update profile');
      }

      // Update localStorage with new user data
      setUser(data.user);
      setUserState(data.user);

      setMessage({ type: 'success', text: 'Profile updated successfully!' });
      setIsEditing(false);

    } catch (error) {
      setMessage({ 
        type: 'error', 
        text: error.message || 'Failed to update profile. Please try again.' 
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Cancel edit mode
  const cancelEdit = () => {
    setIsEditing(false);
    setEditForm({
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      phone: user.phone
    });
    setMessage({ type: '', text: '' });
  };

  // Handle logout
  const handleLogout = () => {
    clearToken();
    setTokenState(null);
    setUserState({});
    setTokenValid(false);
    navigate('/');
  };

  return (
    <div className="profile-container">
      <Navbar />

      <div className="profile-main">
        <div className="profile-content">
          {/* Profile Header */}
          <div className="profile-header">
            <div className="profile-cover" style={{ backgroundImage: `url(${dairyImage})` }}>
              <div className="cover-overlay"></div>
              <div className="cover-content">
                <h2 className="cover-title">Dairy Farm Management</h2>
                <p className="cover-subtitle">Fresh & Organic Products</p>
              </div>
              <div className="profile-avatar">
                <span className="avatar-text">{user.firstName?.charAt(0)}{user.lastName?.charAt(0)}</span>
              </div>
            </div>
            <div className="profile-info">
              <h1>{user.firstName} {user.lastName}</h1>
              <p className="user-email">{user.email}</p>
              <div className="profile-meta">
                <span className="member-since">Member since {new Date().toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</span>
                <span className="user-role">{user.role}</span>
              </div>
            </div>
            <div className="profile-actions">
              <button className="btn btn-primary" onClick={() => setIsEditing(true)}>
                Edit Profile
              </button>
            </div>
          </div>

          {/* Message Display */}
          {message.text && (
            <div className={`message ${message.type}`}>
              <span className="message-icon">
                {message.type === 'success' ? '✅' : '⚠️'}
              </span>
              {message.text}
            </div>
          )}

          {/* Profile Information */}
          <div className="profile-details">
            <div className="details-section">
              <div className="section-header">
                <h2>Personal Information</h2>
              </div>

              {!isEditing ? (
                // Display Mode
                <div className="info-grid">
                  <div className="info-item">
                    <label>First Name</label>
                    <span>{user.firstName}</span>
                  </div>
                  <div className="info-item">
                    <label>Last Name</label>
                    <span>{user.lastName}</span>
                  </div>
                  <div className="info-item">
                    <label>Email Address</label>
                    <span className="email-display">{user.email}</span>
                  </div>
                  <div className="info-item">
                    <label>Phone Number</label>
                    <span>{user.phone}</span>
                  </div>
                  <div className="info-item">
                    <label>Account Role</label>
                    <span className="role-badge">{user.role}</span>
                  </div>
                </div>
              ) : (
                // Edit Mode
                <form onSubmit={handleProfileUpdate} className="edit-form">
                  <div className="info-grid">
                    <div className="info-item">
                      <label>First Name</label>
                      <input
                        type="text"
                        name="firstName"
                        value={editForm.firstName}
                        onChange={handleEditChange}
                        placeholder="Enter first name"
                        required
                      />
                    </div>
                    <div className="info-item">
                      <label>Last Name</label>
                      <input
                        type="text"
                        name="lastName"
                        value={editForm.lastName}
                        onChange={handleEditChange}
                        placeholder="Enter last name"
                        required
                      />
                    </div>
                    <div className="info-item">
                      <label>Email Address</label>
                      <input
                        type="email"
                        name="email"
                        value={editForm.email}
                        onChange={handleEditChange}
                        placeholder="Enter email address"
                        required
                      />
                    </div>
                    <div className="info-item">
                      <label>Phone Number</label>
                      <input
                        type="tel"
                        name="phone"
                        value={editForm.phone}
                        onChange={handleEditChange}
                        placeholder="Enter phone number"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-actions">
                    <button type="submit" className="btn btn-primary" disabled={isLoading}>
                      {isLoading ? 'Saving...' : 'Save Changes'}
                    </button>
                    <button type="button" className="btn btn-secondary" onClick={cancelEdit}>
                      Cancel
                    </button>
                  </div>
                </form>
              )}
            </div>

            {/* Account Actions */}
            <div className="account-actions">
              <h2>Account</h2>
              <div className="action-buttons">
                <button className="btn btn-outline">
                  Change Password
                </button>
                <button className="btn btn-outline">
                  Email Preferences
                </button>
                <button className="btn btn-danger" onClick={handleLogout}>
                  Sign Out
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Profile;
