import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "./BuyMilk.css";

const BuyMilk = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [sortBy, setSortBy] = useState("name");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  // Sample product data - in a real app, this would come from an API
  useEffect(() => {
    const sampleProducts = [
      {
        id: 1,
        name: "Organic Whole Milk",
        category: "milk",
        price: 4.99,
        originalPrice: 5.99,
        image: "🥛",
        description: "Fresh, organic whole milk from grass-fed cows",
        rating: 4.8,
        reviews: 124,
        inStock: true,
        quantity: 1,
        unit: "1L",
        farm: "Green Valley Farm",
        delivery: "Same day",
        organic: true,
        lactoseFree: false
      },
      {
        id: 2,
        name: "Lactose-Free Milk",
        category: "milk",
        price: 5.49,
        originalPrice: 6.49,
        image: "🥛",
        description: "Lactose-free milk for sensitive stomachs",
        rating: 4.6,
        reviews: 89,
        inStock: true,
        quantity: 1,
        unit: "1L",
        farm: "Sunrise Dairy",
        delivery: "Next day",
        organic: false,
        lactoseFree: true
      },
      {
        id: 3,
        name: "Premium Yogurt",
        category: "yogurt",
        price: 3.99,
        originalPrice: 4.99,
        image: "🍶",
        description: "Creamy, probiotic-rich yogurt",
        rating: 4.9,
        reviews: 156,
        inStock: true,
        quantity: 1,
        unit: "500g",
        farm: "Meadow Brook Farm",
        delivery: "Same day",
        organic: true,
        lactoseFree: false
      },
      {
        id: 4,
        name: "Fresh Cheese",
        category: "cheese",
        price: 6.99,
        originalPrice: 7.99,
        image: "🧀",
        description: "Aged cheddar cheese, perfect for cooking",
        rating: 4.7,
        reviews: 67,
        inStock: true,
        quantity: 1,
        unit: "250g",
        farm: "Heritage Farm",
        delivery: "Next day",
        organic: true,
        lactoseFree: false
      },
      {
        id: 5,
        name: "Butter",
        category: "butter",
        price: 4.49,
        originalPrice: 5.49,
        image: "🧈",
        description: "Rich, creamy butter from grass-fed cows",
        rating: 4.5,
        reviews: 92,
        inStock: true,
        quantity: 1,
        unit: "250g",
        farm: "Green Valley Farm",
        delivery: "Same day",
        organic: true,
        lactoseFree: false
      },
      {
        id: 6,
        name: "Cream",
        category: "cream",
        price: 3.99,
        originalPrice: 4.99,
        image: "🥛",
        description: "Heavy cream for cooking and baking",
        rating: 4.4,
        reviews: 78,
        inStock: false,
        quantity: 1,
        unit: "500ml",
        farm: "Sunrise Dairy",
        delivery: "Next day",
        organic: false,
        lactoseFree: false
      }
    ];
    setProducts(sampleProducts);
    setFilteredProducts(sampleProducts);
  }, []);

  // Filter and search products
  useEffect(() => {
    let filtered = products.filter(product => {
      const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.farm.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === "all" || product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    });

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "price-low":
          return a.price - b.price;
        case "price-high":
          return b.price - a.price;
        case "rating":
          return b.rating - a.rating;
        case "name":
        default:
          return a.name.localeCompare(b.name);
      }
    });

    setFilteredProducts(filtered);
  }, [products, searchTerm, selectedCategory, sortBy]);

  // Add to cart
  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  // Remove from cart
  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  // Update cart quantity
  const updateCartQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
    } else {
      setCart(cart.map(item =>
        item.id === productId
          ? { ...item, quantity: newQuantity }
          : item
      ));
    }
  };

  // Calculate cart total
  const cartTotal = cart.reduce((total, item) => total + (item.price * item.quantity), 0);

  // Categories for filtering
  const categories = [
    { value: "all", label: "All Products" },
    { value: "milk", label: "Milk" },
    { value: "yogurt", label: "Yogurt" },
    { value: "cheese", label: "Cheese" },
    { value: "butter", label: "Butter" },
    { value: "cream", label: "Cream" }
  ];

  return (
    <div className="buy-milk-container">
      <Navbar />
      
      {/* Hero Section */}
      <div className="buy-hero">
        <div className="buy-hero-content">
          <h1>Fresh Dairy Products</h1>
          <p>Discover our premium selection of farm-fresh dairy products</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="buy-main">
        <div className="buy-sidebar">
          {/* Search and Filters */}
          <div className="search-section">
            <input
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filter-section">
            <h3>Categories</h3>
            <div className="category-filters">
              {categories.map(category => (
                <label key={category.value} className="category-filter">
                  <input
                    type="radio"
                    name="category"
                    value={category.value}
                    checked={selectedCategory === category.value}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                  />
                  <span>{category.label}</span>
                </label>
              ))}
            </div>
          </div>

          <div className="filter-section">
            <h3>Sort By</h3>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="sort-select"
            >
              <option value="name">Name A-Z</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Highest Rated</option>
            </select>
          </div>

          {/* Cart Summary */}
          <div className="cart-summary">
            <h3>Shopping Cart ({cart.length})</h3>
            {cart.length === 0 ? (
              <p className="empty-cart">Your cart is empty</p>
            ) : (
              <>
                <div className="cart-items">
                  {cart.map(item => (
                    <div key={item.id} className="cart-item">
                      <div className="cart-item-info">
                        <span className="cart-item-name">{item.name}</span>
                        <span className="cart-item-price">${item.price}</span>
                      </div>
                      <div className="cart-item-controls">
                        <button
                          onClick={() => updateCartQuantity(item.id, item.quantity - 1)}
                          className="quantity-btn"
                        >
                          -
                        </button>
                        <span className="quantity">{item.quantity}</span>
                        <button
                          onClick={() => updateCartQuantity(item.id, item.quantity + 1)}
                          className="quantity-btn"
                        >
                          +
                        </button>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="remove-btn"
                        >
                          ×
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
                <div className="cart-total">
                  <strong>Total: ${cartTotal.toFixed(2)}</strong>
                </div>
                <button className="checkout-btn">Proceed to Checkout</button>
              </>
            )}
          </div>
        </div>

        {/* Product Grid */}
        <div className="products-section">
          <div className="products-header">
            <h2>Available Products</h2>
            <p>{filteredProducts.length} products found</p>
          </div>

          <div className="products-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <div className="product-image">
                  <span className="product-emoji">{product.image}</span>
                  {!product.inStock && (
                    <div className="out-of-stock">Out of Stock</div>
                  )}
                  {product.originalPrice > product.price && (
                    <div className="discount-badge">
                      {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                    </div>
                  )}
                </div>
                
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <p className="product-description">{product.description}</p>
                  
                  <div className="product-meta">
                    <span className="farm-name">🏡 {product.farm}</span>
                    <span className="delivery-info">🚚 {product.delivery}</span>
                  </div>
                  
                  <div className="product-features">
                    {product.organic && <span className="feature-badge organic">Organic</span>}
                    {product.lactoseFree && <span className="feature-badge lactose-free">Lactose Free</span>}
                  </div>
                  
                  <div className="product-rating">
                    <span className="stars">{'★'.repeat(Math.floor(product.rating))}</span>
                    <span className="rating-text">{product.rating} ({product.reviews} reviews)</span>
                  </div>
                  
                  <div className="product-pricing">
                    <span className="current-price">${product.price}</span>
                    {product.originalPrice > product.price && (
                      <span className="original-price">${product.originalPrice}</span>
                    )}
                    <span className="unit">{product.unit}</span>
                  </div>
                  
                  <button
                    className={`add-to-cart-btn ${!product.inStock ? 'disabled' : ''}`}
                    onClick={() => addToCart(product)}
                    disabled={!product.inStock}
                  >
                    {!product.inStock ? 'Out of Stock' : 'Add to Cart'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default BuyMilk;
