import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "./BuyMilk.css";

const BuyMilk = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [sortBy, setSortBy] = useState("name");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  // Sample milk products with litre-based quantities
  useEffect(() => {
    const sampleProducts = [
      {
        id: 1,
        name: "Organic Whole Milk",
        category: "whole",
        price: 4.99,
        originalPrice: 5.99,
        image: "🥛",
        description: "Fresh, organic whole milk from grass-fed cows. Rich in nutrients and natural flavor.",
        rating: 4.8,
        reviews: 124,
        inStock: true,
        basePrice: 4.99, // Price per litre
        unit: "litre",
        farm: "Green Valley Farm",
        delivery: "Same day",
        organic: true,
        lactoseFree: false,
        fatContent: "3.25%",
        source: "Grass-fed cows",
        bestBefore: "7 days",
        storage: "Refrigerate at 2-4°C"
      },
      {
        id: 2,
        name: "Lactose-Free Milk",
        category: "lactose-free",
        price: 5.49,
        originalPrice: 6.49,
        image: "🥛",
        description: "Lactose-free milk for sensitive stomachs. Same great taste, easier digestion.",
        rating: 4.6,
        reviews: 89,
        inStock: true,
        basePrice: 5.49, // Price per litre
        unit: "litre",
        farm: "Sunrise Dairy",
        delivery: "Next day",
        organic: false,
        lactoseFree: true,
        fatContent: "2%",
        source: "Conventional cows",
        bestBefore: "10 days",
        storage: "Refrigerate at 2-4°C"
      },
      {
        id: 3,
        name: "Skimmed Milk",
        category: "skimmed",
        price: 3.99,
        originalPrice: 4.99,
        image: "🥛",
        description: "Low-fat skimmed milk. Perfect for health-conscious consumers and cooking.",
        rating: 4.4,
        reviews: 67,
        inStock: true,
        basePrice: 3.99, // Price per litre
        unit: "litre",
        farm: "Meadow Brook Farm",
        delivery: "Same day",
        organic: true,
        lactoseFree: false,
        fatContent: "0.1%",
        source: "Grass-fed cows",
        bestBefore: "7 days",
        storage: "Refrigerate at 2-4°C"
      },
      {
        id: 4,
        name: "2% Reduced Fat Milk",
        category: "reduced-fat",
        price: 4.29,
        originalPrice: 5.29,
        image: "🥛",
        description: "Balanced 2% milk with reduced fat content. Great taste with fewer calories.",
        rating: 4.5,
        reviews: 92,
        inStock: true,
        basePrice: 4.29, // Price per litre
        unit: "litre",
        farm: "Heritage Farm",
        delivery: "Next day",
        organic: false,
        lactoseFree: false,
        fatContent: "2%",
        source: "Conventional cows",
        bestBefore: "7 days",
        storage: "Refrigerate at 2-4°C"
      },
      {
        id: 5,
        name: "Chocolate Milk",
        category: "flavored",
        price: 5.99,
        originalPrice: 6.99,
        image: "🥛",
        description: "Delicious chocolate milk. Perfect treat for kids and adults alike.",
        rating: 4.7,
        reviews: 156,
        inStock: true,
        basePrice: 5.99, // Price per litre
        unit: "litre",
        farm: "Green Valley Farm",
        delivery: "Same day",
        organic: false,
        lactoseFree: false,
        fatContent: "2%",
        source: "Conventional cows",
        bestBefore: "7 days",
        storage: "Refrigerate at 2-4°C"
      },
      {
        id: 6,
        name: "Strawberry Milk",
        category: "flavored",
        price: 5.99,
        originalPrice: 6.99,
        image: "🥛",
        description: "Sweet strawberry milk. Natural strawberry flavor with creamy texture.",
        rating: 4.3,
        reviews: 78,
        inStock: false,
        basePrice: 5.99, // Price per litre
        unit: "litre",
        farm: "Sunrise Dairy",
        delivery: "Next day",
        organic: false,
        lactoseFree: false,
        fatContent: "2%",
        source: "Conventional cows",
        bestBefore: "7 days",
        storage: "Refrigerate at 2-4°C"
      }
    ];
    setProducts(sampleProducts);
    setFilteredProducts(sampleProducts);
  }, []);

  // Filter and search products
  useEffect(() => {
    let filtered = products.filter(product => {
      const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.farm.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === "all" || product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    });

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "price-low":
          return a.basePrice - b.basePrice;
        case "price-high":
          return b.basePrice - a.basePrice;
        case "rating":
          return b.rating - a.rating;
        case "name":
        default:
          return a.name.localeCompare(b.name);
      }
    });

    setFilteredProducts(filtered);
  }, [products, searchTerm, selectedCategory, sortBy]);

  // Add to cart with custom quantity
  const addToCart = (product, quantity = 1) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + quantity }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: quantity }]);
    }
  };

  // Remove from cart
  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  // Update cart quantity
  const updateCartQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
    } else {
      setCart(cart.map(item =>
        item.id === productId
          ? { ...item, quantity: newQuantity }
          : item
      ));
    }
  };

  // Calculate cart total
  const cartTotal = cart.reduce((total, item) => total + (item.basePrice * item.quantity), 0);

  // Categories for filtering
  const categories = [
    { value: "all", label: "All Milk Types" },
    { value: "whole", label: "Whole Milk" },
    { value: "skimmed", label: "Skimmed Milk" },
    { value: "reduced-fat", label: "Reduced Fat" },
    { value: "lactose-free", label: "Lactose Free" },
    { value: "flavored", label: "Flavored Milk" }
  ];

  return (
    <div className="buy-milk-container">
      <Navbar />
      
      {/* Hero Section */}
      <div className="buy-hero">
        <div className="buy-hero-content">
          <h1>Fresh Milk by the Litre</h1>
          <p>Choose your preferred milk type and quantity. Fresh from our farms to your doorstep.</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="buy-main">
        <div className="buy-sidebar">
          {/* Search and Filters */}
          <div className="search-section">
            <input
              type="text"
              placeholder="Search milk types..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filter-section">
            <h3>Milk Types</h3>
            <div className="category-filters">
              {categories.map(category => (
                <label key={category.value} className="category-filter">
                  <input
                    type="radio"
                    name="category"
                    value={category.value}
                    checked={selectedCategory === category.value}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                  />
                  <span>{category.label}</span>
                </label>
              ))}
            </div>
          </div>

          <div className="filter-section">
            <h3>Sort By</h3>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="sort-select"
            >
              <option value="name">Name A-Z</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Highest Rated</option>
            </select>
          </div>

          {/* Cart Summary */}
          <div className="cart-summary">
            <h3>Shopping Cart ({cart.length})</h3>
            {cart.length === 0 ? (
              <p className="empty-cart">Your cart is empty</p>
            ) : (
              <>
                <div className="cart-items">
                  {cart.map(item => (
                    <div key={item.id} className="cart-item">
                      <div className="cart-item-info">
                        <span className="cart-item-name">{item.name}</span>
                        <span className="cart-item-price">${(item.basePrice * item.quantity).toFixed(2)}</span>
                      </div>
                      <div className="cart-item-details">
                        <span className="cart-item-quantity">{item.quantity} litres</span>
                        <span className="cart-item-unit-price">${item.basePrice}/litre</span>
                      </div>
                      <div className="cart-item-controls">
                        <button
                          onClick={() => updateCartQuantity(item.id, item.quantity - 0.5)}
                          className="quantity-btn"
                          disabled={item.quantity <= 0.5}
                        >
                          -
                        </button>
                        <span className="quantity">{item.quantity}L</span>
                        <button
                          onClick={() => updateCartQuantity(item.id, item.quantity + 0.5)}
                          className="quantity-btn"
                        >
                          +
                        </button>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="remove-btn"
                        >
                          ×
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
                <div className="cart-total">
                  <strong>Total: ${cartTotal.toFixed(2)}</strong>
                  <div className="cart-summary-info">
                    Total Litres: {cart.reduce((total, item) => total + item.quantity, 0).toFixed(1)}L
                  </div>
                </div>
                <button className="checkout-btn">Proceed to Checkout</button>
              </>
            )}
          </div>
        </div>

        {/* Product Grid */}
        <div className="products-section">
          <div className="products-header">
            <h2>Available Milk Products</h2>
            <p>{filteredProducts.length} milk types found - All sold by the litre</p>
          </div>

          <div className="products-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <div className="product-image">
                  <span className="product-emoji">{product.image}</span>
                  {!product.inStock && (
                    <div className="out-of-stock">Out of Stock</div>
                  )}
                  {product.originalPrice > product.basePrice && (
                    <div className="discount-badge">
                      {Math.round(((product.originalPrice - product.basePrice) / product.originalPrice) * 100)}% OFF
                    </div>
                  )}
                </div>
                
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <p className="product-description">{product.description}</p>
                  
                  <div className="product-specs">
                    <div className="spec-item">
                      <span className="spec-label">Fat Content:</span>
                      <span className="spec-value">{product.fatContent}</span>
                    </div>
                    <div className="spec-item">
                      <span className="spec-label">Source:</span>
                      <span className="spec-value">{product.source}</span>
                    </div>
                    <div className="spec-item">
                      <span className="spec-label">Best Before:</span>
                      <span className="spec-value">{product.bestBefore}</span>
                    </div>
                  </div>
                  
                  <div className="product-meta">
                    <span className="farm-name">🏡 {product.farm}</span>
                    <span className="delivery-info">🚚 {product.delivery}</span>
                  </div>
                  
                  <div className="product-features">
                    {product.organic && <span className="feature-badge organic">Organic</span>}
                    {product.lactoseFree && <span className="feature-badge lactose-free">Lactose Free</span>}
                  </div>
                  
                  <div className="product-rating">
                    <span className="stars">{'★'.repeat(Math.floor(product.rating))}</span>
                    <span className="rating-text">{product.rating} ({product.reviews} reviews)</span>
                  </div>
                  
                  <div className="product-pricing">
                    <span className="current-price">${product.basePrice}</span>
                    {product.originalPrice > product.basePrice && (
                      <span className="original-price">${product.originalPrice}</span>
                    )}
                    <span className="unit">per litre</span>
                  </div>
                  
                  <div className="quantity-selector">
                    <label htmlFor={`quantity-${product.id}`}>Select Quantity (Litres):</label>
                    <div className="quantity-input-group">
                      <button
                        className="quantity-btn-small"
                        onClick={() => {
                          const input = document.getElementById(`quantity-${product.id}`);
                          const currentValue = parseFloat(input.value) || 0;
                          if (currentValue > 0.5) {
                            input.value = (currentValue - 0.5).toFixed(1);
                          }
                        }}
                      >
                        -
                      </button>
                      <input
                        id={`quantity-${product.id}`}
                        type="number"
                        min="0.5"
                        step="0.5"
                        defaultValue="1"
                        className="quantity-input"
                        placeholder="1.0"
                      />
                      <button
                        className="quantity-btn-small"
                        onClick={() => {
                          const input = document.getElementById(`quantity-${product.id}`);
                          const currentValue = parseFloat(input.value) || 0;
                          input.value = (currentValue + 0.5).toFixed(1);
                        }}
                      >
                        +
                      </button>
                    </div>
                  </div>
                  
                  <button
                    className={`add-to-cart-btn ${!product.inStock ? 'disabled' : ''}`}
                    onClick={() => {
                      const quantity = parseFloat(document.getElementById(`quantity-${product.id}`).value) || 1;
                      addToCart(product, quantity);
                    }}
                    disabled={!product.inStock}
                  >
                    {!product.inStock ? 'Out of Stock' : 'Add to Cart'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default BuyMilk;
