import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { authAPI } from '../services/api';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import './Profile.css';

const Profile = () => {
  const { user, updateUser, logout } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  
  // Form states
  const [editForm, setEditForm] = useState({
    firstName: user?.firstName || '',
    lastName: user?.lastName || '',
    email: user?.email || '',
    phone: user?.phone || ''
  });
  
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  // Handle edit form changes
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle password form changes
  const handlePasswordInputChange = (e) => {
    const { name, value } = e.target;
    setPasswordForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Validate edit form
  const validateEditForm = () => {
    if (!editForm.firstName.trim() || !editForm.lastName.trim() || !editForm.email.trim() || !editForm.phone.trim()) {
      setMessage({ type: 'error', text: 'All fields are required' });
      return false;
    }
    if (editForm.firstName.trim().length < 2) {
      setMessage({ type: 'error', text: 'First name must be at least 2 characters' });
      return false;
    }
    if (editForm.lastName.trim().length < 2) {
      setMessage({ type: 'error', text: 'Last name must be at least 2 characters' });
      return false;
    }
    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(editForm.email.trim())) {
      setMessage({ type: 'error', text: 'Please enter a valid email address' });
      return false;
    }
    return true;
  };

  // Validate password form
  const validatePasswordForm = () => {
    if (!passwordForm.currentPassword || !passwordForm.newPassword || !passwordForm.confirmPassword) {
      setMessage({ type: 'error', text: 'All password fields are required' });
      return false;
    }
    if (passwordForm.newPassword.length < 8) {
      setMessage({ type: 'error', text: 'New password must be at least 8 characters' });
      return false;
    }
    if (!/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(passwordForm.newPassword)) {
      setMessage({ type: 'error', text: 'Password must contain uppercase, lowercase, and number' });
      return false;
    }
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      setMessage({ type: 'error', text: 'New passwords do not match' });
      return false;
    }
    return true;
  };

  // Handle profile update
  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    
    if (!validateEditForm()) return;
    
    setIsLoading(true);
    setMessage({ type: '', text: '' });
    
    try {
      const response = await authAPI.updateProfile(editForm);
      updateUser(response.user);
      setMessage({ type: 'success', text: 'Profile updated successfully!' });
      setIsEditing(false);
    } catch (error) {
      setMessage({ type: 'error', text: error.message || 'Failed to update profile' });
    } finally {
      setIsLoading(false);
    }
  };

  // Handle password change
  const handlePasswordChange = async (e) => {
    e.preventDefault();
    
    if (!validatePasswordForm()) return;
    
    setIsLoading(true);
    setMessage({ type: '', text: '' });
    
    try {
      await authAPI.changePassword(passwordForm);
      setMessage({ type: 'success', text: 'Password changed successfully!' });
      setIsChangingPassword(false);
      setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
    } catch (error) {
      setMessage({ type: 'error', text: error.message || 'Failed to change password' });
    } finally {
      setIsLoading(false);
    }
  };

  // Cancel edit mode
  const cancelEdit = () => {
    setIsEditing(false);
    setEditForm({
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      phone: user.phone
    });
    setMessage({ type: '', text: '' });
  };

  // Cancel password change
  const cancelPasswordChange = () => {
    setIsChangingPassword(false);
    setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
    setMessage({ type: '', text: '' });
  };

  return (
    <div className="profile-container">
      <Navbar />
      
      <div className="profile-main">
        <div className="profile-content">
          {/* Profile Header */}
          <div className="profile-header">
            <div className="profile-avatar">
              <span className="avatar-text">{user.firstName.charAt(0)}{user.lastName.charAt(0)}</span>
            </div>
            <h1>User Profile</h1>
            <p>Manage your account information and preferences</p>
          </div>

          {/* Message Display */}
          {message.text && (
            <div className={`message ${message.type}`}>
              <span className="message-icon">
                {message.type === 'success' ? '✅' : '⚠️'}
              </span>
              {message.text}
            </div>
          )}

          {/* Profile Information */}
          <div className="profile-info">
            <div className="info-section">
              <div className="section-header">
                <h2>Personal Information</h2>
                {!isEditing && (
                  <button 
                    className="btn btn-outline"
                    onClick={() => setIsEditing(true)}
                  >
                    ✏️ Edit Profile
                  </button>
                )}
              </div>

              {!isEditing ? (
                // Display Mode
                <div className="info-grid">
                  <div className="info-item">
                    <label>First Name</label>
                    <span>{user.firstName}</span>
                  </div>
                  <div className="info-item">
                    <label>Last Name</label>
                    <span>{user.lastName}</span>
                  </div>
                  <div className="info-item">
                    <label>Email Address</label>
                    <span className="email-display">{user.email}</span>
                  </div>
                  <div className="info-item">
                    <label>Phone Number</label>
                    <span>{user.phone}</span>
                  </div>
                  <div className="info-item">
                    <label>Account Role</label>
                    <span className="role-badge">{user.role}</span>
                  </div>
                  <div className="info-item">
                    <label>Member Since</label>
                    <span>{new Date(user.createdAt || Date.now()).toLocaleDateString()}</span>
                  </div>
                </div>
              ) : (
                // Edit Mode
                <form onSubmit={handleProfileUpdate} className="edit-form">
                  <div className="info-grid">
                    <div className="info-item">
                      <label>First Name</label>
                      <input
                        type="text"
                        name="firstName"
                        value={editForm.firstName}
                        onChange={handleEditChange}
                        placeholder="Enter first name"
                        required
                      />
                    </div>
                    <div className="info-item">
                      <label>Last Name</label>
                      <input
                        type="text"
                        name="lastName"
                        value={editForm.lastName}
                        onChange={handleEditChange}
                        placeholder="Enter last name"
                        required
                      />
                    </div>
                    <div className="info-item">
                      <label>Email Address</label>
                      <input
                        type="email"
                        name="email"
                        value={editForm.email}
                        onChange={handleEditChange}
                        placeholder="Enter email address"
                        required
                      />
                    </div>
                    <div className="info-item">
                      <label>Phone Number</label>
                      <input
                        type="tel"
                        name="phone"
                        value={editForm.phone}
                        onChange={handleEditChange}
                        placeholder="Enter phone number"
                        required
                      />
                    </div>
                  </div>
                  <div className="form-actions">
                    <button type="submit" className="btn btn-primary" disabled={isLoading}>
                      {isLoading ? 'Saving...' : 'Save Changes'}
                    </button>
                    <button type="button" className="btn btn-secondary" onClick={cancelEdit}>
                      Cancel
                    </button>
                  </div>
                </form>
              )}
            </div>

            {/* Security Section */}
            <div className="info-section">
              <div className="section-header">
                <h2>Security & Privacy</h2>
                {!isChangingPassword && (
                  <button 
                    className="btn btn-outline"
                    onClick={() => setIsChangingPassword(true)}
                  >
                    🔒 Change Password
                  </button>
                )}
              </div>

              {isChangingPassword && (
                <form onSubmit={handlePasswordChange} className="password-form">
                  <div className="info-grid">
                    <div className="info-item">
                      <label>Current Password</label>
                                      <input
                  type="password"
                  name="currentPassword"
                  value={passwordForm.currentPassword}
                  onChange={handlePasswordInputChange}
                  placeholder="Enter current password"
                  required
                />
                    </div>
                    <div className="info-item">
                      <label>New Password</label>
                                      <input
                  type="password"
                  name="newPassword"
                  value={passwordForm.newPassword}
                  onChange={handlePasswordInputChange}
                  placeholder="Enter new password"
                  required
                />
                    </div>
                    <div className="info-item">
                      <label>Confirm New Password</label>
                                      <input
                  type="password"
                  name="confirmPassword"
                  value={passwordForm.confirmPassword}
                  onChange={handlePasswordInputChange}
                  placeholder="Confirm new password"
                  required
                />
                    </div>
                  </div>
                  <div className="password-requirements">
                    <p>Password must contain:</p>
                    <ul>
                      <li>At least 8 characters</li>
                      <li>One uppercase letter</li>
                      <li>One lowercase letter</li>
                      <li>One number</li>
                    </ul>
                  </div>
                  <div className="form-actions">
                    <button type="submit" className="btn btn-primary" disabled={isLoading}>
                      {isLoading ? 'Updating...' : 'Update Password'}
                    </button>
                    <button type="button" className="btn btn-secondary" onClick={cancelPasswordChange}>
                      Cancel
                    </button>
                  </div>
                </form>
              )}
            </div>

            {/* Account Actions */}
            <div className="account-actions">
              <h2>Account Actions</h2>
              <div className="action-buttons">
                <button className="btn btn-danger" onClick={logout}>
                  🚪 Logout
                </button>
                <button className="btn btn-warning">
                  ⚠️ Delete Account
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Profile;
