import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // We'll create this CSS file for styling

function Home() {
  return (
    <div className="home-container">
      {/* Navbar */}
      <header className="navbar">
        <h1 className="logo">DairyFarm</h1>
        <nav>
          <Link to="/login" className="nav-link">Login</Link>
          <Link to="/register" className="nav-link">Register</Link>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <h2>Fresh Milk Delivered to Your Doorstep</h2>
        <p>Join our platform to buy or sell fresh dairy products.</p>
        <div className="hero-buttons">
          <Link to="/buyer" className="btn btn-primary">Buy Milk</Link>
          <Link to="/seller" className="btn btn-secondary">Sell Milk</Link>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; {new Date().getFullYear()} DairyFarm. All Rights Reserved.</p>
      </footer>
    </div>
  );
}

export default Home;
